/*
* Casalimpia Custom Tool: generate_payment_link
* Genera enlace de pago para servicios en carrito
* Variables: $conversation_id, $user_email
*/

const generatePaymentLink = async () => {
  try {
    const conversationId = $conversation_id || $flow.sessionId;
    const userEmail = $user_email;

    // Validar formato de email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(userEmail)) {
      return JSON.stringify({
        success: false,
        error: 'Invalid email format',
        message: 'âŒ Por favor proporciona un email vÃ¡lido (ejemplo: usuario@correo.com)'
      });
    }

    // Si usas API externa de Casalimpia
    if (process.env.CASALIMPIA_API_BASE && process.env.CASALIMPIA_API_TOKEN) {
      const response = await fetch(`${process.env.CASALIMPIA_API_BASE}/payment/generate`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${process.env.CASALIMPIA_API_TOKEN}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          conversation_id: conversationId,
          user_email: userEmail
        })
      });

      if (!response.ok) {
        throw new Error(`API Error: ${response.status}`);
      }

      const paymentData = await response.json();

      return JSON.stringify({
        success: true,
        payment_link: paymentData.payment_url,
        total_amount: paymentData.total,
        services_count: paymentData.services_count,
        message: `âœ… *Â¡Perfecto!* He generado tu enlace de pago.\n\nğŸ’³ *Total*: $${paymentData.total.toLocaleString()} COP\nğŸ“§ *Email*: ${userEmail}\nğŸ›ï¸ *Servicios*: ${paymentData.services_count}\n\nğŸ‘† Puedes completar la compra de forma segura haciendo clic en el enlace.\n\nÂ¡Gracias por elegir Casalimpia! ğŸ§¹âœ¨`
      });
    }

    // SimulaciÃ³n para testing sin API
    const cartItems = $flow.state?.cart_items || [];
    const mockTotal = 113150 * cartItems.length; // Precio base por servicio
    const mockPaymentUrl = `https://payment.casalimpia.com/pay/${conversationId}`;

    return JSON.stringify({
      success: true,
      payment_link: mockPaymentUrl,
      total_amount: mockTotal,
      services_count: cartItems.length,
      user_email: userEmail,
      conversation_id: conversationId,
      message: `âœ… *Â¡Perfecto!* He generado tu enlace de pago.\n\nğŸ’³ *Total*: $${mockTotal.toLocaleString()} COP\nğŸ“§ *Email*: ${userEmail}\nğŸ›ï¸ *Servicios*: ${cartItems.length}\n\n${mockPaymentUrl}\n\nğŸ‘† Puedes completar la compra de forma segura haciendo clic en el enlace.\n\nÂ¡Gracias por elegir Casalimpia! ğŸ§¹âœ¨`
    });

  } catch (error) {
    console.error('Error generating payment link:', error);
    return JSON.stringify({ 
      success: false,
      error: 'Failed to generate payment link',
      message: 'âŒ Error al generar enlace de pago. Intenta nuevamente o contacta a soporte.'
    });
  }
};

const result = await generatePaymentLink();
return result;
