/*
* Casalimpia Custom Tool: get_sic_customer_packages
* Obtiene servicios existentes del cliente por cÃ©dula desde la API de Viernes for Business
* Variable: $customer_id
*/
const fetch = require('node-fetch');

/*
* Casalimpia Custom Tool: get_sic_customer_packages
* Obtiene servicios existentes del cliente por cÃ©dula desde la API de Viernes for Business
* Variable: $customer_id
*/

const getSicCustomerPackages = async () => {
  try {
    const customerId = $customer_id;
    const baseUrl = $vars.api_url;

    // Validar que customer_id estÃ© presente
    if (!customerId) {
      return JSON.stringify({
        error: 'CÃ©dula requerida',
        message: 'Por favor proporciona tu nÃºmero de cÃ©dula para buscar tus servicios existentes.'
      });
    }

    console.log(`Getting customer packages for customer_id: ${customerId}`);

    // Llamar a la API de Viernes for Business
    const apiUrl = `${baseUrl}/tools/execute?base=casalimpia_sic&tool=get_sic_customer_packages&pass_db=true`;

    const response = await fetch(apiUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        customer_id: customerId
      })
    });

    if (!response.ok) {
      console.error(`API Error: ${response.status} - ${response.statusText}`);
      throw new Error(`API Error: ${response.status}`);
    }

    const apiResult = await response.json();
    console.log('API Response:', apiResult);

    // Verificar el status de la respuesta
    if (apiResult.status !== 'success') {
      return JSON.stringify({
        error: 'API Error',
        message: apiResult.message || 'Error al obtener servicios del cliente.',
        customer_id: customerId,
        status: apiResult.status
      });
    }

    // Procesar las citas de la respuesta (cambio de 'data' a 'appointments')
    let appointments = apiResult.appointments || [];

    // Filtrar y formatear citas segÃºn la estructura real de la API
    if (Array.isArray(appointments)) {
      appointments = appointments.map((appointment, index) => {
        // Formatear horario para mostrar mejor
        const horarioText = appointment.horario === 'completa' ? 'Jornada Completa (8 Horas)' :
                           appointment.horario === 'manana' ? 'Jornada MaÃ±ana (4 Horas)' :
                           appointment.horario === 'tarde' ? 'Jornada Tarde (4 Horas)' :
                           appointment.horario;

        return {
          id: appointment.idTransaccion,
          servicio: `Limpieza - ${horarioText}`,
          fecha_actual: appointment.fecha,
          hora_actual: appointment.horario,
          experta_actual: appointment.nombreOperario,
          ciudad: appointment.ciudad,
          estado: 'programado', // Asumimos que estÃ¡n programados si vienen en la respuesta
          precio: null, // No viene en la respuesta actual
          ref_externa: appointment.refExterna,
          telefono: appointment.telefono,
          email: appointment.email,
          fullName: appointment.fullName
        };
      });
    }

    return JSON.stringify({
      customer_id: customerId,
      packages: appointments,
      total_packages: appointments.length,
      message: appointments.length > 0
        ? `ğŸ“‹ EncontrÃ© *${appointments.length}* servicio(s) programados para reagendar:\n\n${appointments.map((pkg, i) => 
            `*${i+1}.* ${pkg.servicio}\nğŸ“… ${pkg.fecha_actual}\nğŸ‘¤ Experta: ${pkg.experta_actual}\nğŸ™ï¸ Ciudad: ${pkg.ciudad}\nğŸ“ Ref: ${pkg.ref_externa}`
          ).join('\n\n')}\n\nÂ¿CuÃ¡l servicio quieres reagendar? ğŸ”„`
        : 'No tienes servicios pendientes para reagendar. Â¿Quieres contratar un servicio nuevo? ğŸ§¹',
      success: true,
      api_status: apiResult.status
    });

  } catch (error) {
    console.error('Error in get_sic_customer_packages:', error);
    throw error;
  }
};

const result = await getSicCustomerPackages();
return result;
