/*
* Casalimpia Custom Tool: get_sic_customer_packages
* Obtiene servicios existentes del cliente por cÃ©dula
* Variable: $cedula
*/

const getSicCustomerPackages = async () => {
  try {
    const cedula = $cedula;

    if (!cedula) {
      return JSON.stringify({
        error: 'CÃ©dula requerida',
        message: 'Por favor proporciona tu nÃºmero de cÃ©dula para buscar tus servicios'
      });
    }

    // Si usas API externa de Casalimpia
    if (process.env.CASALIMPIA_API_BASE && process.env.CASALIMPIA_API_TOKEN) {
      const response = await fetch(`${process.env.CASALIMPIA_API_BASE}/customer/packages`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${process.env.CASALIMPIA_API_TOKEN}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          cedula: cedula
        })
      });

      if (!response.ok) {
        if (response.status === 404) {
          return JSON.stringify({
            packages: [],
            total_packages: 0,
            message: `No se encontraron servicios para la cÃ©dula *${cedula}*. Â¿Quieres contratar un servicio nuevo? ğŸ§¹`
          });
        }
        throw new Error(`API Error: ${response.status}`);
      }

      const packages = await response.json();

      // Filtrar solo servicios activos/programados
      const activePackages = packages.filter(pkg => 
        pkg.estado && !['completado', 'cancelado'].includes(pkg.estado.toLowerCase())
      );

      return JSON.stringify({
        packages: activePackages,
        total_packages: activePackages.length,
        customer_cedula: cedula,
        message: activePackages.length > 0 
          ? `EncontrÃ© *${activePackages.length}* servicio(s) para reagendar` 
          : 'No tienes servicios pendientes para reagendar'
      });
    }

    // Datos de ejemplo para testing sin API
    const mockPackages = [
      {
        id: 'PKG001',
        servicio: 'Limpieza - Jornada MaÃ±ana (4 Horas)',
        fecha_actual: '2025-01-15',
        hora_actual: '8:00 AM - 12:00 PM',
        experta_actual: 'MarÃ­a GonzÃ¡lez',
        ciudad: 'BOGOTA',
        estado: 'programado',
        precio: 113150
      },
      {
        id: 'PKG002',
        servicio: 'Limpieza - Jornada Completa (8 Horas)',
        fecha_actual: '2025-01-20',
        hora_actual: '8:00 AM - 5:00 PM',
        experta_actual: 'Carmen LÃ³pez',
        ciudad: 'BOGOTA',
        estado: 'programado',
        precio: 159568
      }
    ];

    return JSON.stringify({
      packages: mockPackages,
      total_packages: mockPackages.length,
      customer_cedula: cedula,
      message: `ğŸ“‹ EncontrÃ© *${mockPackages.length}* servicio(s) programados:\n\n${mockPackages.map((pkg, i) => 
        `*${i+1}.* ${pkg.servicio}\nğŸ“… ${pkg.fecha_actual} (${pkg.hora_actual})\nğŸ‘¤ Experta: ${pkg.experta_actual}\nğŸ™ï¸ Ciudad: ${pkg.ciudad}`
      ).join('\n\n')}\n\nÂ¿CuÃ¡l servicio quieres reagendar? ğŸ”„`
    });

  } catch (error) {
    console.error('Error getting customer packages:', error);
    return JSON.stringify({ 
      error: 'Failed to get customer packages',
      packages: [],
      message: 'Error al buscar tus servicios. Verifica tu cÃ©dula e intenta nuevamente.'
    });
  }
};

const result = await getSicCustomerPackages();
return result;
