/*
* Casalimpia Custom Tool: get_sic_experts_list
* Obtiene expertas de SIC Casalimpia disponibles desde la API de Viernes for Business
* Variables: $ciudad_servicio, $fecha_servicio, $jornada_servicio, $preferencia_experta
*/

const getSicExpertsList = async () => {
  try {
    const ciudadServicio = $ciudad_servicio;
    const fechaServicio = $fecha_servicio;
    const jornadaServicio = $jornada_servicio;
    const preferenciaExperta = $preferencia_experta || "";
    const baseUrl = $vars.api_url;

    // Validar parÃ¡metros requeridos
    if (!ciudadServicio || !fechaServicio || !jornadaServicio) {
      return JSON.stringify({
        error: 'ParÃ¡metros requeridos faltantes',
        message: 'Se requiere ciudad de servicio, fecha de servicio y jornada de servicio.'
      });
    }

    // Validar formato de fecha
    const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
    if (!dateRegex.test(fechaServicio)) {
      return JSON.stringify({
        error: 'Formato de fecha invÃ¡lido',
        message: 'La fecha debe estar en formato YYYY-MM-DD (ejemplo: 2025-08-15).'
      });
    }

    console.log(`Getting experts list for city: ${ciudadServicio}, date: ${fechaServicio}, jornada: ${jornadaServicio}`);

    // Preparar el body con los parÃ¡metros
    const requestBody = {
      ciudad_servicio: ciudadServicio,
      fecha_servicio: fechaServicio,
      jornada_servicio: jornadaServicio,
      preferencia_experta: preferenciaExperta
    };

    // Llamar a la API de Viernes for Business
    const apiUrl = `${baseUrl}/tools/execute?base=casalimpia_sic&tool=get_sic_experts_list&pass_db=true`;

    const response = await fetch(apiUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(requestBody)
    });

    if (!response.ok) {
      console.error(`API Error: ${response.status} - ${response.statusText}`);
      throw new Error(`API Error: ${response.status}`);
    }

    const apiResult = await response.json();
    console.log('API Response:', apiResult);

    // Verificar el status de la respuesta
    if (apiResult.status !== 'success') {
      return JSON.stringify({
        error: 'API Error',
        message: apiResult.message || 'Error al obtener lista de expertas.',
        ciudad_servicio: ciudadServicio,
        status: apiResult.status
      });
    }

    // Procesar las expertas de la respuesta
    let experts = apiResult.data || apiResult.experts || [];

    // Filtrar y formatear expertas segÃºn la estructura real de la API
    if (Array.isArray(experts)) {
      experts = experts.map((expert, index) => {
        return {
          id: expert.id || index + 1,
          nombre: expert.name || expert.nombre,
          cedula: expert.cedula || expert.id_cedula,
          experiencia: expert.experience || expert.experiencia,
          rating: expert.rating || expert.calificacion,
          especialidad: expert.specialty || expert.especialidad,
          disponible: expert.available !== undefined ? expert.available : true
        };
      });
    }

    // Mensaje dinÃ¡mico segÃºn si hay preferencia o no
    let message = `ğŸ‘¥ Expertas disponibles para el *${fechaServicio}* en *${ciudadServicio}*`;
    if (preferenciaExperta && preferenciaExperta.trim() !== "") {
      message += `\nğŸ” BÃºsqueda: "${preferenciaExperta}"`;
    }

    if (experts.length > 0) {
      message += `\n\n${experts.map((expert, i) => 
        `*${i+1}.* ${expert.nombre}\nğŸ‘¤ CÃ©dula: ${expert.cedula}\nâ­ Rating: ${expert.rating || 'N/A'}\nğŸ“‹ Especialidad: ${expert.especialidad || 'Limpieza general'}`
      ).join('\n\n')}`;
      message += '\n\nÂ¿CuÃ¡l experta prefieres para tu servicio? ğŸ‘©â€ğŸ”§';
    } else {
      message += '\n\nâŒ No hay expertas disponibles para esta fecha y jornada.';
    }

    return JSON.stringify({
      ciudad_servicio: ciudadServicio,
      fecha_servicio: fechaServicio,
      jornada_servicio: jornadaServicio,
      preferencia_experta: preferenciaExperta,
      experts: experts,
      total_experts: experts.length,
      message: message,
      success: true,
      api_status: apiResult.status
    });

  } catch (error) {
    console.error('Error in get_sic_experts_list:', error);
    throw error;
  }
};

const result = await getSicExpertsList();
return result;
